import React,{useState} from "react";
import { StyleSheet, Text, View, ImageBackground, Modal, Button,Linking} from 'react-native';
import styles from "./styles";
import StyleButton from "../StyleButton";
import MicroButton from "../MicroButton";
import * as Permissions from 'expo-permissions';
import { Audio } from 'expo-av';

const EyeItem=()=>{
 
//pentru website
  //const [transcript, setTranscript] = useState('');
    
  // const startRecognition = () => {
  //   const recognition = new window.webkitSpeechRecognition();
  //   recognition.interimResults = true;

  //   recognition.onresult = e => {
  //     const interimTranscript = Array.from(e.results)
  //       .map(result => result[0])
  //       .map(result => result.transcript)
  //       .join('');

  //     setTranscript(interimTranscript);
  //   };

  //   recognition.start();
    
  // };
  //for phone
  const [transcript, setTranscript] = useState('');
  const [recording, setRecording] = useState(null);

  const startRecognition = async () => {
    try {
      const { status } = await Permissions.askAsync(Permissions.AUDIO_RECORDING);
      if (status !== 'granted') {
        alert('Permission to access microphone was denied');
        return;
      }

      const recording = new Audio.Recording();
      await recording.prepareToRecordAsync(Audio.RECORDING_OPTIONS_PRESET_HIGH_QUALITY);
      await recording.startAsync();
      setRecording(recording);
    } catch (error) {
      console.error(error);
    }
  };

  const stopRecognition = async () => {
    try {
      await recording.stopAndUnloadAsync();
      const uri = recording.getURI();
      setTranscript('');
      setRecording(null);
      //console.log(Recording saved to ${uri});

      // Call a speech-to-text API to convert the recording to text
      // ...

    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    return () => {
      if (recording) {
        recording.stopAndUnloadAsync();
        setRecording(null);
      }
    };
  }, [recording]);

  const handleLinkPress = () => {
    const link = 'https://youtu.be/X4sicgZ1rus';
    Linking.openURL(link);
  };
return (
    <View style={styles.eyeContainer}>
      

    <ImageBackground 
    source={require('D:\\Facultate Anul III\\SeverinReactApp\\reactappn\\assets\\images\\Backphoto2.jpg')}
    style={styles.image}
    />

    

    <View style={styles.titles}>
      <Text style={styles.title}>We Become Your Eyes</Text>
      <Text style={styles.subtitles}>Here To Help You</Text>
    </View>
    <View style={styles.buttMicrophone}>
      <MicroButton/>

    </View>
    <View style={styles.buttonsContainer}>
    <StyleButton style={{bottom:'50'}}
    type="primary" 
    content={"Use My Eye"}
    onPress={handleLinkPress}
   
    />
    </View>
    <View style={styles.buttonRecord}>
    <StyleButton 
    type="secondary" 
    content={"Start Recording"}
    onPress={startRecognition}
   
    />
    <Text style={styles.textAs} id="convert_text">{transcript}</Text>
    </View>
    {/* <>
  <StyleButton styles={styles.butonsContainer}
    id="click_to_record"
    title="Click to Record"
    onPress={startRecognition}
  />
  <Text style={styles.textAs} id="convert_text">{transcript}</Text>
</> */}
{/* <View style={styles.buttonRecord}>
    <StyleButton 
    type="secondary" 
    content={"Click to Record"}
    onPress={handleLinkPress}
    
    />
    </View> */}
    
  </View>
);

};


export default EyeItem;
