import { StatusBar } from 'expo-status-bar';
import React,{useState} from 'react';
// import {NavigationContainer} from '@react-navigation/native';
// import {createStackNavigator} from '@react-navigation/native-stack';
import { StyleSheet, Text, View, ImageBackground, Modal, Button} from 'react-native';
import EyeItem from './components/EyeItem';
import Header from './components/Header';


export default function App() {

    const [showModal, setShowModal] = useState(true);

    const toggleModal = () => {
      setShowModal(!showModal);
    };


  return (

    <View style={styles.container}>

       <Modal visible={showModal} animationType="slide" transparent>
      <View style={styles.modalContainer}>
        <View style={styles.modalContent}>
          <Text  style={styles.modalText}>Welcome to the EYES</Text>
          <Text  style={styles.modalText}>Here are some instructions:</Text>
          <Text  numberOfLines={2} style={styles.modalText}>If you want to Start the App say OPEN</Text>

          <Button title="Close" onPress={toggleModal} />
        </View>
      </View>
    </Modal>

      <Header/>

      <EyeItem/>

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  // popUp:{
  //   alignItems: 'center',
  //   justifyContent: 'center',
  // },


  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalContent: {
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 10,
    alignItems: 'center',
  },
  modalText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 16,
    flexWrap: 'wrap',
    width: '100%',
  },


});
