import React,{useState} from "react";
import {View, Text, ImageBackground, Pressable,Linking} from 'react-native';
import styles from "./styles";
import Video from 'react-native-video';


const StyleButton=(props)=>{
    // const handleLinkPress = () => {
    //     const link = 'https://youtu.be/f0RepUfPcco';
    //     Linking.openURL(link);
    //   };
    const [selectedVideo, setSelectedVideo] = useState(null);

  const handlePress1 = () => {
    setSelectedVideo('D:\\Facultate Anul III\\SeverinReactApp\\reactappn\\assets\\videos\\VID-20230403-WA0001.mp4');
  };

    const type = props.type;
    const content = props.content;
    const onPress = props.onPress;
    //const{type, content, onPress} = props;
    const backgroundColor = type === 'primary' ? 'black' : 'white';
    const textColor = type === 'primary' ?  'white' : 'black';
    return (
        <View style={styles.container}>
            <Pressable
            style={[styles.button,{backgroundColor: backgroundColor}]}
            onPress={handlePress1}
            >
                <Text style={[styles.text,{color:textColor}]}>{content}</Text>
            
            </Pressable>
            {selectedVideo && (
        <Video source={{ uri: selectedVideo }} style={{ height: 300 }} />
      )}
      </View>
    );

};

export default StyleButton;
