import { StatusBar } from 'expo-status-bar';
import React,{useState} from 'react';
import { StyleSheet, Text, View, ImageBackground, Modal, Button} from 'react-native';
import EyeItem from './components/EyeItem';
import Header from './components/Header';
import SpeechRecognition, {
  useSpeechRecognition
} from "react-speech-recognition";


export default function App() {

    const [showModal, setShowModal] = useState(true);

    const toggleModal = () => {
      setShowModal(!showModal);
    };

    const { transcript, listening ,resetTranscript,browserSupportsSpeechRecognition } = useSpeechRecognition();
    SpeechRecognition.startListening({ continuous: true });
    if (!browserSupportsSpeechRecognition) {
      return <span>NU merge aici boss</span>
    } 


  return (

    <View style={styles.container}>

       <Modal visible={showModal} animationType="slide" transparent>
      <View style={styles.modalContainer}>
        <View style={styles.modalContent}>
          <Text  style={styles.modalText}>Welcome to the EYES!</Text>
          <Text  style={styles.modalText}>If you want to start the app</Text>
          <Text  style={styles.modalText}>say 'OPEN'</Text>

          <Button  title="Close" onPress={toggleModal}  />
        </View>
      </View>
    </Modal>

      <Header/>

      <EyeItem/>

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  // popUp:{
