import { StyleSheet } from "react-native";

const styles=StyleSheet.create({
    eyeContainer:{
        width:'100%',
        height:'100%',
    
      },
    
      titles: {
        marginTop: '30%',
        width:'100%',
        alignItems: 'center',
      },
    
      title: {
        fontSize: 35,
        fontWeight:'500',
      },
    
      subtitles: {
        fontSize:16,
        color: 'grey',
    
      },
    
      image: {
        width:'100%',
        height:'100%',
        resizeMode: 'cover',
        position: 'absolute',
      },

      buttonsContainer:{
        position: 'absolute',
        bottom: 50,
        width:'100%',
      },

      buttMicrophone:{
        position: 'absolute',
        bottom: 270,
        width:'100%',

      },

      modalContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'transparent',
      },
      modalContent: {
        backgroundColor: 'rgba(255, 51, 51, 0.3),',
        padding: 20,
        borderRadius: 10,
        alignItems: 'center',
      },
      modalText: {
        fontSize: 24,
        fontWeight: '400',
        textAlign: 'center',
        marginBottom: 16,
       
      },

});

export default styles;
